  const generateQueueData = () => {
    const data = [];
    const now = Date.now();
    for (let i = 0; i < 24; i++) {
      data.push({
        time: format(new Date(now - (24 - i) * 3600000), 'HH:mm'),
        position: Math.max(1, Math.floor(Math.random() * 50) - i * 2),
        estimatedWait: Math.max(5, 120 - i * 4)
      });
    }
