import { useState, useCallback, useEffect } from "react";
import { useSearchParams, useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import { RefreshCw, CheckCircle, XCircle, Clock, Play, Activity, Search, Bell, Plus, Settings, Menu, X, Users, BarChart3, Zap } from "lucide-react";
import { Header } from "@/components/dashboard/header";
import { StatsCards } from "@/components/dashboard/stats-cards";
import { JobsTable } from "@/components/dashboard/jobs-table";
import { TimelineView } from "@/components/dashboard/timeline-view";
import { AnalyticsCharts } from "@/components/dashboard/analytics-charts";
import { Sidebar } from "@/components/dashboard/sidebar";
import { Button } from "@/components/ui/button";
import { queryClient } from "@/lib/queryClient";
import { JobSimulator } from "@/lib/job-simulator";
import { useUpdateJobStatus } from "@/hooks/use-jobs";
import { useToast } from "@/hooks/use-toast";
import { ActiveSessions } from "@/components/dashboard/active-sessions";
import { BackendAdvisor } from "@/components/dashboard/backend-advisor";
import { AllBackendsView } from "@/components/dashboard/all-backends-view";
import { SessionForm } from "@/components/dashboard/session-form";
import { NotificationPanel } from "@/components/dashboard/notification-panel";
import { AnimatePresence } from "framer-motion";
import { useJobNotifications } from "@/hooks/use-job-notifications";
import { useTheme } from "@/hooks/use-theme";
import { Card, CardHeader, CardContent } from "@/components/ui/card";
import { JobStatusDistribution } from "@/components/dashboard/job-status-distribution";
import { LiveActivityFeed } from "@/components/dashboard/live-activity-feed";
import { AIAssistant } from "@/components/ai/ai-assistant";
import { NewsPanel } from "@/components/dashboard/news-panel";

const containerVariants = {
  hidden: { opacity: 0 },
  visible: {
    opacity: 1,
    transition: {
      duration: 0.3,
      staggerChildren: 0.1,
    },
  },
};

const itemVariants = {
  hidden: { opacity: 0, y: 20 },
  visible: {
    opacity: 1,
    y: 0,
    transition: { duration: 0.5 },
  },
};




export default function Dashboard() {
  const [searchQuery, setSearchQuery] = useState("");
  const [refreshInterval, setRefreshInterval] = useState(10);
  const updateJobStatus = useUpdateJobStatus();
  const { toast } = useToast();
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [showSessionForm, setShowSessionForm] = useState(false);
  const [showNotifications, setShowNotifications] = useState(false);
  const [searchParams, setSearchParams] = useSearchParams();
  const navigate = useNavigate();
  const currentView = searchParams.get('view') || 'overview'; // Default to 'overview'

  // Initialize job notifications
  useJobNotifications();

  // Set up job simulator
  useEffect(() => {
    const simulator = JobSimulator.getInstance();

    simulator.onStatusChange((jobId, status, error) => {
      updateJobStatus.mutate(
        { id: jobId, status, error },
        {
          onSuccess: () => {
            const statusMessages = {
              running: "Job started running",
              done: "Job completed successfully",
              failed: "Job failed",
              cancelled: "Job was cancelled",
              queued: "Job queued",
            };

            const statusIcons = {
              running: <Play className="w-4 h-4" />,
              done: <CheckCircle className="w-4 h-4" />,
              failed: <XCircle className="w-4 h-4" />,
              cancelled: <XCircle className="w-4 h-4" />,
              queued: <Clock className="w-4 h-4" />,
            };

            toast({
              title: "Job Status Update",
              description: `${jobId}: ${statusMessages[status]}`,
              variant: status === "failed" ? "destructive" : "default",
            });
          },
        }
      );
    });
  }, [updateJobStatus, toast]);

  // Set up auto-refresh for IBM Quantum API endpoints
  useEffect(() => {
    if (refreshInterval === 0) return;

    const interval = setInterval(() => {
      queryClient.invalidateQueries({ queryKey: ["https://runtime.quantum-computing.ibm.com/jobs"] });
      queryClient.invalidateQueries({ queryKey: ["https://runtime.quantum-computing.ibm.com/analytics/stats"] });
      queryClient.invalidateQueries({ queryKey: ["https://runtime.quantum-computing.ibm.com/backends"] });
      queryClient.invalidateQueries({ queryKey: ["https://runtime.quantum-computing.ibm.com/sessions"] });
    }, refreshInterval * 1000);

    return () => clearInterval(interval);
  }, [refreshInterval]);

  const handleSearch = useCallback((query: string) => {
    setSearchQuery(query);
  }, []);

  const handleRefreshIntervalChange = useCallback((interval: number) => {
    setRefreshInterval(interval);
  }, []);

  const handleManualRefresh = useCallback(() => {
    queryClient.invalidateQueries({ queryKey: ["https://runtime.quantum-computing.ibm.com/jobs"] });
    queryClient.invalidateQueries({ queryKey: ["https://runtime.quantum-computing.ibm.com/analytics/stats"] });
    queryClient.invalidateQueries({ queryKey: ["https://runtime.quantum-computing.ibm.com/backends"] });
    queryClient.invalidateQueries({ queryKey: ["https://runtime.quantum-computing.ibm.com/sessions"] });
  }, []);

  const handleViewChange = (view: string) => {
    setSearchParams({ view: view });
  };

  const handleOpenSessionForm = () => {
    setShowSessionForm(true);
  };

  const handleCloseSessionForm = () => {
    setShowSessionForm(false);
  };

  const handleNotificationToggle = () => {
    setShowNotifications(!showNotifications);
  };

  // Conditionally render different views (now handled by renderCurrentView)
  if (currentView === 'all-backends') {
    return (
      <motion.div
        className="min-h-screen bg-gradient-to-br from-gray-50 via-white to-blue-50 dark:from-gray-900 dark:via-gray-800 dark:to-blue-900/20"
        variants={containerVariants}
        initial="hidden"
        animate="visible"
      >
        {/* Floating background elements */}
        <div className="fixed inset-0 overflow-hidden pointer-events-none">
          <motion.div
            className="absolute top-1/4 left-1/4 w-96 h-96 bg-gradient-to-r from-blue-400/10 to-purple-400/10 rounded-full blur-3xl"
            animate={{
              scale: [1, 1.1, 1],
              rotate: [0, 180, 360],
            }}
            transition={{
              duration: 20,
              repeat: Infinity,
              ease: "linear"
            }}
          />
          <motion.div
            className="absolute top-3/4 right-1/4 w-64 h-64 bg-gradient-to-r from-purple-400/10 to-pink-400/10 rounded-full blur-3xl"
            animate={{
              scale: [1.1, 1, 1.1],
              rotate: [360, 180, 0],
            }}
            transition={{
              duration: 15,
              repeat: Infinity,
              ease: "linear"
            }}
          />
        </div>

        <Header
          onSearch={handleSearch}
          onRefreshIntervalChange={handleRefreshIntervalChange}
          onManualRefresh={handleManualRefresh}
          onViewChange={handleViewChange}
          onNotificationToggle={handleNotificationToggle}
        />

        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
          <motion.div variants={itemVariants}>
            <AllBackendsView onBack={() => handleViewChange('overview')} />
          </motion.div>
        </div>
      </motion.div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-50 dark:from-gray-900 dark:via-blue-900/20 dark:to-indigo-900/20">
      <Header
        onSearch={setSearchQuery}
        onRefreshIntervalChange={setRefreshInterval}
        onManualRefresh={handleManualRefresh}
        onViewChange={handleViewChange}
        onNotificationToggle={() => setShowNotifications(!showNotifications)}
      />

      <div className="flex">
        <Sidebar
          currentView={currentView}
          onViewChange={handleViewChange}
        />

        <main className="flex-1 p-6 lg:p-8">
          <div className="max-w-7xl mx-auto">
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5 }}
              className="mb-8"
            >
              <div className="flex items-center justify-between">
                <div>
                  <h1 className="text-3xl font-bold text-gray-900 dark:text-white mb-2">
                    {currentView === 'overview' && 'Quantum Dashboard Overview'}
                    {currentView === 'jobs' && 'Quantum Jobs Management'}
                    {currentView === 'backends' && 'Quantum Backends Status'}
                    {currentView === 'sessions' && 'Active Sessions'}
                    {currentView === 'analytics' && 'Analytics & Insights'}
                  </h1>
                  <p className="text-gray-600 dark:text-gray-400">
                    Real-time monitoring of IBM Quantum Cloud resources
                  </p>
                </div>
                <div className="flex items-center space-x-2">
                  <div className="flex items-center space-x-2 px-3 py-1 bg-green-100 dark:bg-green-900/30 rounded-full">
                    <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse"></div>
                    <span className="text-sm font-medium text-green-700 dark:text-green-300">Live Data</span>
                  </div>
                </div>
              </div>
            </motion.div>

            <AnimatePresence mode="wait">
              <motion.div
                key={currentView}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                transition={{ duration: 0.3 }}
              >
                {/* Only show StatsCards once at the top for all views */}
                <div className="mb-6">
                  <StatsCards />
                </div>

                {/* Show search results indicator */}
                {searchQuery && (
                  <div className="mb-4 p-3 bg-blue-50 dark:bg-blue-900/20 rounded-lg border border-blue-200 dark:border-blue-800">
                    <div className="flex items-center justify-between">
                      <div className="flex items-center space-x-2">
                        <Search className="w-4 h-4 text-blue-600" />
                        <span className="text-sm font-medium text-blue-900 dark:text-blue-100">
                          Searching for: "{searchQuery}"
                        </span>
                      </div>
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => setSearchQuery("")}
                        className="text-blue-600 hover:text-blue-700"
                      >
                        Clear
                      </Button>
                    </div>
                  </div>
                )}

                {/* Comprehensive Status Overview */}
                {currentView === 'overview' && (
                  <div className="space-y-6">
                    {/* Analytics Charts Section */}
                    <div className="mb-8">
                      <div className="flex items-center justify-between mb-6">
                        <div>
                          <h2 className="text-2xl font-bold text-gray-900 dark:text-white">Analytics Overview</h2>
                          <p className="text-gray-600 dark:text-gray-400">Real-time insights and performance metrics</p>
                        </div>
                        <Button 
                          variant="outline" 
                          onClick={() => handleViewChange('analytics')}
                          className="flex items-center space-x-2"
                        >
                          <BarChart3 className="w-4 h-4" />
                          <span>View Full Analytics</span>
                        </Button>
                      </div>
                      <AnalyticsCharts />
                    </div>

                    <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                      <Card className="bg-white/70 dark:bg-gray-800/70 backdrop-blur-sm border-gray-200/50 dark:border-gray-700/50">
                        <CardHeader>
                          <h3 className="text-lg font-semibold text-gray-900 dark:text-white flex items-center">
                            <Activity className="w-5 h-5 mr-2 text-blue-500" />
                            Job Status Distribution
                          </h3>
                        </CardHeader>
                        <CardContent>
                          <JobStatusDistribution />
                        </CardContent>
                      </Card>

                      <Card className="bg-white/70 dark:bg-gray-800/70 backdrop-blur-sm border-gray-200/50 dark:border-gray-700/50">
                        <CardHeader>
                          <h3 className="text-lg font-semibold text-gray-900 dark:text-white flex items-center">
                            <Zap className="w-5 h-5 mr-2 text-green-500" />
                            Live Activity Feed
                          </h3>
                        </CardHeader>
                        <CardContent>
                          <LiveActivityFeed />
                        </CardContent>
                      </Card>
                    </div>

                    <Card className="bg-white/70 dark:bg-gray-800/70 backdrop-blur-sm border-gray-200/50 dark:border-gray-700/50">
                      <CardHeader>
                        <h3 className="text-lg font-semibold text-gray-900 dark:text-white flex items-center">
                          <BarChart3 className="w-5 h-5 mr-2 text-purple-500" />
                          Recent Jobs Overview
                        </h3>
                      </CardHeader>
                      <CardContent>
                        <JobsTable searchQuery={searchQuery} />
                      </CardContent>
                    </Card>
                  </div>
                )}
                {currentView === 'analytics' && (
                  <div className="space-y-6">
                    <div className="text-center mb-8">
                      <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-2">
                        Analytics Dashboard
                      </h2>
                      <p className="text-gray-600 dark:text-gray-400">
                        Comprehensive insights into your quantum job performance
                      </p>
                    </div>
                    <AnalyticsCharts />
                  </div>
                )}
                {currentView === 'jobs' && <JobsTable searchQuery={searchQuery} />}
                {currentView === 'sessions' && <ActiveSessions />}
                {currentView === 'backends' && <AllBackendsView onBack={() => handleViewChange('overview')} />}
              </motion.div>
            </AnimatePresence>
          </div>
        </main>
      </div>

      <AnimatePresence>
        {showNotifications && (
          <NotificationPanel
            isOpen={showNotifications}
            onClose={() => setShowNotifications(false)}
          />
        )}
      </AnimatePresence>

      {/* News Panel - floating on all dashboard views */}
      <NewsPanel />

      {/* AI Assistant - floating on all dashboard views */}
      <AIAssistant />
    </div>
  );
}

server/ai-collaboration-service.ts.content:
import OpenAI from 'openai';
import type { 
  Workspace, Project, WorkspaceMember, ProjectCollaborator, Job, Backend,
  AiRecommendation, InsertAiRecommendation, UserProfile, Challenge,
  Experiment, CollaborationMetric, QuantumInsight
} from '@shared/schema';
import { openaiService } from './openai-service';

export class AICollaborationService {
  private client: OpenAI | undefined;
  private isConfigured: boolean = false;

  constructor() {
    const apiKey = process.env.OPENAI_API_KEY;
    if (apiKey) {
      this.client = new OpenAI({ apiKey });
      this.isConfigured = true;
    }
  }

  // AI-Powered Team Matching and Recommendations
  async generateTeamMatchingRecommendations(
    user: UserProfile,
    workspaces: Workspace[],
    allUsers: UserProfile[]
  ): Promise<Partial<AiRecommendation>[]> {
    if (!this.isConfigured || !this.client) {
      return this.getFallbackTeamRecommendations(user, workspaces);
    }

    try {
      const prompt = `As an AI collaboration expert, analyze this user profile and recommend optimal team matches and collaborations:

User Profile:
- Name: ${user.displayName}
- Skills: ${user.skills?.join(', ') || 'No skills listed'}
- Interests: ${user.interests?.join(', ') || 'No interests listed'}
- Experience: ${user.experience}
- Current Points: ${user.totalPoints}
- Level: ${user.level}
- Bio: ${user.bio || 'No bio'}

Available Workspaces:
${workspaces.map(w => `- ${w.name}: ${w.description} (Status: ${w.status})`).join('\n')}

Available Users for Collaboration:
${allUsers.slice(0, 20).map(u => `- ${u.displayName}: Skills: ${u.skills?.join(', ') || 'None'}, Experience: ${u.experience}`).join('\n')}

Generate 5 intelligent recommendations for:
1. Best workspace matches based on skills/interests
2. Ideal collaborators for quantum projects
3. Mentorship opportunities (as mentor or mentee)
4. Skill development suggestions
5. Team formation for challenging projects

Return as JSON array with format:
[{
  "type": "team_match|project_suggestion|mentorship|skill_development",
  "title": "Brief recommendation title",
  "description": "Detailed explanation with reasoning",
  "confidence": 85,
  "data": {"workspaceId": "optional", "userIds": ["optional"], "skills": ["optional"]}
}]`;

      const response = await this.client!.chat.completions.create({
        // the newest OpenAI model is "gpt-5" which was released August 7, 2025. do not change this unless explicitly requested by the user
        model: 'gpt-5',
        messages: [{ role: 'user', content: prompt }],
        response_format: { type: "json_object" },
      });

      const content = response.choices[0]?.message?.content;
      if (content) {
        const recommendations = JSON.parse(content);
        return recommendations.map((rec: any) => ({
          userId: user.userId,
          type: rec.type,
          title: rec.title,
          description: rec.description,
          confidence: rec.confidence,
          data: rec.data,
          status: 'pending',
          expiresAt: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000), // 7 days
        }));
      }
    } catch (error) {
      console.error('AI team matching error:', error);
    }

    return this.getFallbackTeamRecommendations(user, workspaces);
  }

  // Smart Project Suggestions
  async generateProjectSuggestions(
    workspace: Workspace,
    members: WorkspaceMember[],
    recentJobs: Job[],
    availableBackends: Backend[]
  ): Promise<Partial<AiRecommendation>[]> {
    if (!this.isConfigured || !this.client) {
      return this.getFallbackProjectSuggestions();
    }

    try {
      const prompt = `As a quantum computing research expert, analyze this workspace and suggest innovative project ideas:

Workspace: ${workspace.name}
Description: ${workspace.description}
Progress: ${workspace.progress}%
Status: ${workspace.status}

Team Members: ${members.length} total
Member Roles: ${members.map(m => `${m.userName} (${m.role})`).join(', ')}

Recent Jobs Analysis:
${recentJobs.slice(0, 10).map(j => `- ${j.name}: ${j.backend}, ${j.qubits} qubits, Status: ${j.status}`).join('\n')}

Available Quantum Backends:
${availableBackends.map(b => `- ${b.name}: ${b.qubits} qubits, Status: ${b.status}, Queue: ${b.queueLength}`).join('\n')}

Generate 4 innovative project suggestions that:
1. Build on current progress and team expertise
2. Utilize available quantum hardware effectively
3. Advance quantum computing research
4. Create learning opportunities for team members

Return as JSON array with format:
[{
  "type": "project_suggestion",
  "title": "Project name",
  "description": "Detailed project description, objectives, and expected outcomes",
  "confidence": 90,
  "data": {
    "difficulty": "beginner|intermediate|advanced",
    "estimatedDuration": "weeks",
    "requiredSkills": ["skill1", "skill2"],
    "recommendedBackend": "backend_name",
    "expectedOutcomes": ["outcome1", "outcome2"]
  }
}]`;

      const response = await this.client!.chat.completions.create({
        // the newest OpenAI model is "gpt-5" which was released August 7, 2025. do not change this unless explicitly requested by the user
        model: 'gpt-5',
        messages: [{ role: 'user', content: prompt }],
        response_format: { type: "json_object" },
      });

      const content = response.choices[0]?.message?.content;
      if (content) {
        const suggestions = JSON.parse(content);
        return suggestions.map((sug: any) => ({
          workspaceId: workspace.id,
          type: sug.type,
          title: sug.title,
          description: sug.description,
          confidence: sug.confidence,
          data: sug.data,
          status: 'pending',
          expiresAt: new Date(Date.now() + 14 * 24 * 60 * 60 * 1000), // 14 days
        }));
      }
    } catch (error) {
      console.error('AI project suggestions error:', error);
    }

    return this.getFallbackProjectSuggestions();
  }

  // Intelligent Resource Allocation
  async generateResourceOptimizations(
    workspaces: Workspace[],
    jobs: Job[],
    backends: Backend[]
  ): Promise<Partial<AiRecommendation>[]> {
    if (!this.isConfigured || !this.client) {
      return this.getFallbackResourceOptimizations();
    }

    try {
      const prompt = `As a quantum resource optimization expert, analyze current usage patterns and recommend improvements:

Active Workspaces:
${workspaces.filter(w => w.status === 'active').map(w => `- ${w.name}: ${w.progress}% complete`).join('\n')}

Recent Job Patterns:
${jobs.slice(0, 20).map(j => `- ${j.backend}: ${j.qubits}q, ${j.shots} shots, ${j.status}, ${j.duration || 0}s`).join('\n')}

Backend Status:
${backends.map(b => `- ${b.name}: ${b.qubits}q, ${b.queueLength} queued, ${b.averageWaitTime}s avg wait, ${b.uptime} uptime`).join('\n')}

Analyze patterns and generate 3 optimization recommendations for:
1. Hardware utilization efficiency
2. Cost reduction strategies  
3. Performance improvements

Return as JSON array with format:
[{
  "type": "resource_optimization",
  "title": "Optimization recommendation",
  "description": "Detailed explanation and implementation steps",
  "confidence": 85,
  "data": {
    "category": "cost|performance|efficiency",
    "expectedSavings": "percentage or time",
    "affectedWorkspaces": ["workspace_id"],
    "implementation": "step by step guide"
  }
}]`;

      const response = await this.client!.chat.completions.create({
        // the newest OpenAI model is "gpt-5" which was released August 7, 2025. do not change this unless explicitly requested by the user
        model: 'gpt-5',
        messages: [{ role: 'user', content: prompt }],
        response_format: { type: "json_object" },
      });

      const content = response.choices[0]?.message?.content;
      if (content) {
        const optimizations = JSON.parse(content);
        return optimizations.map((opt: any) => ({
          type: opt.type,
          title: opt.title,
          description: opt.description,
          confidence: opt.confidence,
          data: opt.data,
          status: 'pending',
          expiresAt: new Date(Date.now() + 30 * 24 * 60 * 60 * 1000), // 30 days
        }));
      }
    } catch (error) {
      console.error('AI resource optimization error:', error);
    }

    return this.getFallbackResourceOptimizations();
  }

  // Advanced Code Review and Optimization
  async generateQuantumCodeReview(
    code: string,
    context: {
      projectName: string;
      backend: string;
      qubits: number;
      collaborators: string[];
    }
  ): Promise<{
    overallScore: number;
    suggestions: Array<{
      type: 'optimization' | 'error' | 'best_practice' | 'collaboration';
      severity: 'info' | 'warning' | 'critical';
      line?: number;
      title: string;
      description: string;
      suggestion: string;
    }>;
    collaborationInsights: Array<{
      aspect: string;
      insight: string;
      recommendation: string;
    }>;
  }> {
    if (!this.isConfigured || !this.client) {
      return this.getFallbackCodeReview();
    }

    try {
      const prompt = `As a senior quantum computing code reviewer with collaboration expertise, analyze this quantum code:

Project: ${context.projectName}
Target Backend: ${context.backend}
Qubits: ${context.qubits}
Collaborators: ${context.collaborators.join(', ')}

Code:
\`\`\`
${code}
\`\`\`

Provide comprehensive analysis including:
1. Overall code quality score (0-100)
2. Specific improvement suggestions
3. Collaboration insights for team development

Return as JSON with format:
{
  "overallScore": 85,
  "suggestions": [
    {
      "type": "optimization|error|best_practice|collaboration",
      "severity": "info|warning|critical",
      "line": 10,
      "title": "Brief issue title",
      "description": "What the issue is",
      "suggestion": "How to fix or improve it"
    }
  ],
  "collaborationInsights": [
    {
      "aspect": "Code Style|Documentation|Complexity",
      "insight": "What this reveals about team collaboration",
      "recommendation": "How to improve team collaboration"
    }
  ]
}`;

      const response = await this.client!.chat.completions.create({
        // the newest OpenAI model is "gpt-5" which was released August 7, 2025. do not change this unless explicitly requested by the user
        model: 'gpt-5',
        messages: [{ role: 'user', content: prompt }],
        response_format: { type: "json_object" },
      });

      const content = response.choices[0]?.message?.content;
      if (content) {
        return JSON.parse(content);
      }
    } catch (error) {
      console.error('AI code review error:', error);
    }

    return this.getFallbackCodeReview();
  }

  // Experiment Design Assistant
  async generateExperimentDesign(
    hypothesis: string,
    workspace: Workspace,
    availableResources: {
      backends: Backend[];
      teamSkills: string[];
      timeframe: string;
    }
  ): Promise<{
    methodology: string;
    experimentPlan: Array<{
      phase: string;
      duration: string;
      tasks: string[];
      resources: string[];
    }>;
    expectedOutcomes: string[];
    riskAssessment: Array<{
      risk: string;
      probability: 'low' | 'medium' | 'high';
      mitigation: string;
    }>;
    collaborationStrategy: {
      roleAssignments: Array<{ role: string; skills: string[]; responsibilities: string[] }>;
      communicationPlan: string;
      milestones: Array<{ milestone: string; timeline: string; deliverables: string[] }>;
    };
  }> {
    if (!this.isConfigured || !this.client) {
      return this.getFallbackExperimentDesign();
    }

    try {
      const prompt = `As a quantum research methodology expert, design a comprehensive experiment:

Hypothesis: ${hypothesis}

Workspace Context:
- Name: ${workspace.name}
- Description: ${workspace.description}
- Current Progress: ${workspace.progress}%

Available Resources:
- Backends: ${availableResources.backends.map(b => `${b.name} (${b.qubits}q)`).join(', ')}
- Team Skills: ${availableResources.teamSkills.join(', ')}
- Timeframe: ${availableResources.timeframe}

Design a complete experiment including methodology, collaboration strategy, and risk management.

Return as JSON with the specified format.`;

      const response = await this.client!.chat.completions.create({
        // the newest OpenAI model is "gpt-5" which was released August 7, 2025. do not change this unless explicitly requested by the user
        model: 'gpt-5',
        messages: [{ role: 'user', content: prompt }],
        response_format: { type: "json_object" },
      });

      const content = response.choices[0]?.message?.content;
      if (content) {
        return JSON.parse(content);
      }
    } catch (error) {
      console.error('AI experiment design error:', error);
    }

    return this.getFallbackExperimentDesign();
  }

  // Dynamic Learning Path Generation
  async generateLearningPath(
    user: UserProfile,
    targetSkills: string[],
    availableChallenges: Challenge[],
    completedAchievements: string[]
  ): Promise<{
    path: Array<{
      step: number;
      title: string;
      description: string;
      type: 'challenge' | 'project' | 'collaboration' | 'mentorship';
      difficulty: 'beginner' | 'intermediate' | 'advanced';
      estimatedTime: string;
      prerequisites: string[];
      resources: string[];
    }>;
    estimatedDuration: string;
    skillProgression: Array<{
      skill: string;
      currentLevel: 'novice' | 'beginner' | 'intermediate' | 'advanced' | 'expert';
      targetLevel: 'novice' | 'beginner' | 'intermediate' | 'advanced' | 'expert';
      milestones: string[];
    }>;
  }> {
    if (!this.isConfigured || !this.client) {
      return this.getFallbackLearningPath(targetSkills);
    }

    try {
      const prompt = `As a quantum education expert, create a personalized learning path:

User Profile:
- Current Skills: ${user.skills?.join(', ') || 'None'}
- Experience Level: ${user.experience}
- Completed Achievements: ${completedAchievements.length} total

Target Skills: ${targetSkills.join(', ')}

Available Challenges:
${availableChallenges.map(c => `- ${c.name}: ${c.difficulty} (${c.category})`).join('\n')}

Create a structured learning path that progressively builds skills through practical challenges and collaborative projects.

Return as JSON with the specified format.`;

      const response = await this.client!.chat.completions.create({
        // the newest OpenAI model is "gpt-5" which was released August 7, 2025. do not change this unless explicitly requested by the user
        model: 'gpt-5',
        messages: [{ role: 'user', content: prompt }],
        response_format: { type: "json_object" },
      });

      const content = response.choices[0]?.message?.content;
      if (content) {
        return JSON.parse(content);
      }
    } catch (error) {
      console.error('AI learning path error:', error);
    }

    return this.getFallbackLearningPath(targetSkills);
  }

  // Fallback methods for when AI is not available
  private getFallbackTeamRecommendations(user: UserProfile, workspaces: Workspace[]): Partial<AiRecommendation>[] {
    return [
      {
        userId: user.userId,
        workspaceId: workspaces[0]?.id || null,
        type: 'team_match',
        title: 'Join Active Quantum ML Research',
        description: 'Based on your profile, you would be a great fit for collaborative quantum machine learning projects.',
        confidence: 80,
        data: { workspaceId: workspaces[0]?.id },
        status: 'pending',
        expiresAt: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000),
      },
