              <div className="h-4 w-4 bg-gradient-to-r from-gray-300 to-gray-200 rounded"></div>
            </CardHeader>
            <CardContent>
              <div className="h-6 bg-gradient-to-r from-gray-300 to-gray-200 rounded w-12 mb-1"></div>
              <div className="h-3 bg-gradient-to-r from-gray-300 to-gray-200 rounded w-20"></div>
            </CardContent>
          </Card>
        ))}
      </div>
    );
  }

  const statsConfig = [
    {
      title: "Total Jobs",
      value: stats?.totalJobs || 0,
      icon: Activity,
      description: "All quantum jobs",
      color: "text-blue-600",
      bgGradient: "from-blue-500/10 to-blue-600/10",
      borderColor: "border-blue-200",
      iconBg: "bg-blue-100",
    },
    {
      title: "Running",
      value: stats?.runningJobs || 0,
      icon: Zap,
      description: "Currently executing",
      color: "text-green-600",
      bgGradient: "from-green-500/10 to-green-600/10",
      borderColor: "border-green-200",
      iconBg: "bg-green-100",
      pulse: true,
    },
    {
      title: "Queued",
      value: stats?.queuedJobs || 0,
      icon: Clock,
      description: "Waiting in queue",
      color: "text-yellow-600",
      bgGradient: "from-yellow-500/10 to-yellow-600/10",
      borderColor: "border-yellow-200",
      iconBg: "bg-yellow-100",
    },
    {
      title: "Completed",
      value: stats?.completedJobs || 0,
      icon: CheckCircle,
      description: "Successfully finished",
      color: "text-blue-600",
      bgGradient: "from-blue-500/10 to-blue-600/10",
      borderColor: "border-blue-200",
      iconBg: "bg-blue-100",
    },
    {
      title: "Failed",
      value: stats?.failedJobs || 0,
      icon: XCircle,
      description: "Execution failed",
      color: "text-red-600",
      bgGradient: "from-red-500/10 to-red-600/10",
      borderColor: "border-red-200",
      iconBg: "bg-red-100",
    },
    {
      title: "Success Rate",
      value: `${stats?.successRate || 0}%`,
      icon: TrendingUp,
      description: "Job success ratio",
      color: "text-purple-600",
      bgGradient: "from-purple-500/10 to-purple-600/10",
      borderColor: "border-purple-200",
      iconBg: "bg-purple-100",
    },
  ];

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-6 gap-4 mb-6">
      {statsConfig.map((stat, index) => (
          <Card 
            key={stat.title} 
