import { useState } from "react";
import { motion } from "framer-motion";
import { X, Plus, Save } from "lucide-react";
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";

interface SessionFormProps {
  onClose: () => void;
}

export function SessionForm({ onClose }: SessionFormProps) {
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    tags: "",
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const { toast } = useToast();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));

      toast({
        title: "Session created",
        description: `Session "${formData.name}" has been created successfully.`,
      });

      onClose();
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to create session",
        variant: "destructive",
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleInputChange = (field: string, value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  return (
    <div className="fixed inset-0 bg-black/50 backdrop-blur-sm flex items-center justify-center z-50 p-4">
      <motion.div
        initial={{ opacity: 0, scale: 0.95 }}
        animate={{ opacity: 1, scale: 1 }}
        exit={{ opacity: 0, scale: 0.95 }}
        transition={{ duration: 0.2 }}
        className="w-full max-w-md"
      >
        <Card className="bg-white dark:bg-gray-800 shadow-2xl">
          <CardHeader className="border-b border-gray-200 dark:border-gray-700">
            <div className="flex items-center justify-between">
              <h2 className="text-xl font-semibold text-gray-900 dark:text-white">Create New Session</h2>
              <Button
                variant="ghost"
                size="icon"
                onClick={onClose}
                className="hover:bg-gray-100 dark:hover:bg-gray-700"
                data-testid="button-close-session-form"
              >
                <X className="w-4 h-4" />
              </Button>
            </div>
          </CardHeader>

          <CardContent className="p-6">
            <form onSubmit={handleSubmit} className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="session-name" className="text-sm font-medium text-gray-700 dark:text-gray-300">
                  Session Name *
                </Label>
                <Input
                  id="session-name"
                  type="text"
                  placeholder="Enter session name"
                  value={formData.name}
                  onChange={(e) => handleInputChange("name", e.target.value)}
                  required
                  className="w-full"
                  data-testid="input-session-name"
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="session-description" className="text-sm font-medium text-gray-700 dark:text-gray-300">
                  Description
                </Label>
                <Textarea
                  id="session-description"
                  placeholder="Enter session description (optional)"
                  value={formData.description}
                  onChange={(e) => handleInputChange("description", e.target.value)}
                  rows={3}
                  className="w-full"
                  data-testid="textarea-session-description"
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="session-tags" className="text-sm font-medium text-gray-700 dark:text-gray-300">
                  Tags
                </Label>
                <Input
                  id="session-tags"
                  type="text"
                  placeholder="Enter tags separated by commas"
                  value={formData.tags}
                  onChange={(e) => handleInputChange("tags", e.target.value)}
                  className="w-full"
                  data-testid="input-session-tags"
                />
                <p className="text-xs text-gray-500 dark:text-gray-400">
                  Separate multiple tags with commas (e.g., quantum, experiment, research)
                </p>
              </div>

              <div className="flex justify-end space-x-3 pt-4">
                <Button type="button" variant="outline" onClick={onClose}>
                  Cancel
                </Button>
                <Button
                  type="submit"
                  className="bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700"
                  disabled={isSubmitting || !formData.name.trim()}
                  data-testid="button-submit-session"
                >
                  {isSubmitting ? (
                    <motion.div
                      animate={{ rotate: 360 }}
                      transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
                      className="w-4 h-4 border-2 border-white border-t-transparent rounded-full mr-2"
                    />
                  ) : (
                    <Save className="w-4 h-4 mr-2" />
                  )}
                  {isSubmitting ? "Creating..." : "Create Session"}
                </Button>
              </div>
            </form>
          </CardContent>
        </Card>
      </motion.div>
    </div>
  );
}

client/src/components/ai/ai-failure-anal.content:
import { useState } from 'react';
import { motion } from 'framer-motion';
import { AlertTriangle, Bot, Lightbulb, Wrench, Shield, RefreshCw, ChevronRight, BookOpen, Zap } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Separator } from '@/components/ui/separator';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { useMutation } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';

interface FailureAnalysis {
  possibleCauses: string[];
  suggestions: string[];
  circuitImprovements: string[];
  preventionTips: string[];
}

interface AIFailureAnalysisProps {
  jobId: string;
  jobName?: string;
  error?: string;
  onRetryWithSuggestion?: (suggestion: string) => void;
}

export function AIFailureAnalysis({ jobId, jobName, error, onRetryWithSuggestion }: AIFailureAnalysisProps) {
  const [analysis, setAnalysis] = useState<FailureAnalysis | null>(null);
  const [showDetailedInstructions, setShowDetailedInstructions] = useState(false);
  const [detailedInstructions, setDetailedInstructions] = useState<string>('');

  const analyzeMutation = useMutation({
    mutationFn: async () => {
      const response = await fetch(`/api/ai/analyze-failure/${jobId}`, {
        method: 'POST'
      });
      return response.json();
    },
    onSuccess: (data) => {
      setAnalysis(data);
    }
  });

  const handleAnalyze = () => {
    analyzeMutation.mutate();
  };

  // Get circuit improvement instructions
  const getCircuitInstructionsMutation = useMutation({
    mutationFn: async () => {
      const response = await fetch(`/api/ai/circuit-instructions/${jobId}`, {
        method: 'POST'
      });
      const data = await response.json();
      return data.instructions;
    },
    onSuccess: (instructions) => {
      setDetailedInstructions(instructions);
      setShowDetailedInstructions(true);
    }
  });

  // Get guided circuit improvements
  const getGuidedImprovementsMutation = useMutation({
    mutationFn: async () => {
      const response = await fetch(`/api/ai/guided-improvements/${jobId}`, {
        method: 'POST'
      });
      return response.json();
    },
    onSuccess: (improvements) => {
      // Apply guided improvements to the current analysis
      if (analysis && improvements) {
        setAnalysis({
          ...analysis,
          circuitImprovements: [...analysis.circuitImprovements, ...improvements]
        });
      }
    }
  });

  return (
    <Card className="bg-gradient-to-r from-red-50 to-orange-50 dark:from-red-900/20 dark:to-orange-900/20 border-red-200 dark:border-red-700">
      <CardHeader className="pb-3">
        <div className="flex items-center gap-3">
          <div className="p-2 bg-gradient-to-r from-red-500 to-orange-500 rounded-lg">
            <AlertTriangle className="w-5 h-5 text-white" />
          </div>
          <div>
            <CardTitle className="text-lg">AI Failure Analysis</CardTitle>
            <p className="text-sm text-gray-600 dark:text-gray-400 mt-1">
              Get insights on why this job failed and how to fix it
            </p>
          </div>
        </div>
      </CardHeader>

      <CardContent className="space-y-4">
        {error && (
          <Alert className="border-orange-200 bg-orange-50 dark:border-orange-700 dark:bg-orange-900/20">
            <AlertTriangle className="h-4 w-4 text-orange-600" />
            <AlertDescription className="text-sm text-orange-800 dark:text-orange-200">
              <strong>Error:</strong> {error}
            </AlertDescription>
          </Alert>
        )}

        {!analysis ? (
          <div className="text-center py-6">
            <Button 
              onClick={handleAnalyze}
              disabled={analyzeMutation.isPending}
              className="bg-gradient-to-r from-red-500 to-orange-500 hover:from-red-600 hover:to-orange-600"
              data-testid="button-analyze-failure"
            >
              <Bot className="w-4 h-4 mr-2" />
              {analyzeMutation.isPending ? 'Analyzing...' : 'Analyze Failure'}
            </Button>
          </div>
        ) : (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3 }}
            className="space-y-4"
          >
            {/* Possible Causes */}
            {analysis.possibleCauses.length > 0 && (
              <div className="space-y-3">
                <div className="flex items-center gap-2">
                  <AlertTriangle className="w-4 h-4 text-red-500" />
                  <h4 className="font-semibold text-sm">Possible Causes</h4>
                </div>
                <div className="space-y-2">
                  {analysis.possibleCauses.map((cause, index) => (
                    <motion.div
                      key={index}
                      initial={{ opacity: 0, x: -20 }}
                      animate={{ opacity: 1, x: 0 }}
                      transition={{ delay: index * 0.1 }}
                      className="flex items-start gap-3 p-3 bg-white/70 dark:bg-gray-800/70 rounded-lg border-l-4 border-red-300"
                    >
                      <ChevronRight className="w-4 h-4 text-red-500 mt-0.5 flex-shrink-0" />
                      <span className="text-sm">{cause}</span>
                    </motion.div>
                  ))}
                </div>
              </div>
            )}

            <Separator />

            {/* Suggestions */}
            {analysis.suggestions.length > 0 && (
              <div className="space-y-3">
                <div className="flex items-center gap-2">
                  <Lightbulb className="w-4 h-4 text-yellow-500" />
                  <h4 className="font-semibold text-sm">Recommended Solutions</h4>
                </div>
                <div className="space-y-2">
                  {analysis.suggestions.map((suggestion, index) => (
                    <motion.div
                      key={index}
                      initial={{ opacity: 0, x: -20 }}
                      animate={{ opacity: 1, x: 0 }}
                      transition={{ delay: index * 0.1 + 0.3 }}
                      className="flex items-start gap-3 p-3 bg-white/70 dark:bg-gray-800/70 rounded-lg border-l-4 border-yellow-300"
                    >
                      <Lightbulb className="w-4 h-4 text-yellow-500 mt-0.5 flex-shrink-0" />
                      <div className="flex-1">
                        <span className="text-sm">{suggestion}</span>
                      </div>
                      {onRetryWithSuggestion && (
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => onRetryWithSuggestion(suggestion)}
                          className="text-xs px-2 py-1"
                          data-testid={`button-apply-suggestion-${index}`}
                        >
                          Try This
                        </Button>
                      )}
                    </motion.div>
                  ))}
                </div>
              </div>
            )}

            <Separator />

            {/* Circuit Improvements */}
            {analysis.circuitImprovements.length > 0 && (
              <div className="space-y-3">
                <div className="flex items-center justify-between">
                  <div className="flex items-center gap-2">
                    <Wrench className="w-4 h-4 text-blue-500" />
                    <h4 className="font-semibold text-sm">Circuit Improvements</h4>
                  </div>
                  <div className="flex gap-2">
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => getCircuitInstructionsMutation.mutate()}
                      disabled={getCircuitInstructionsMutation.isPending}
                      className="text-xs px-3 py-1 h-7"
                      data-testid="button-circuit-instructions"
                    >
                      <BookOpen className="w-3 h-3 mr-1" />
                      {getCircuitInstructionsMutation.isPending ? 'Loading...' : 'Get Instructions'}
                    </Button>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => getGuidedImprovementsMutation.mutate()}
                      disabled={getGuidedImprovementsMutation.isPending}
                      className="text-xs px-3 py-1 h-7"
                      data-testid="button-guided-improvements"
                    >
                      <Zap className="w-3 h-3 mr-1" />
                      {getGuidedImprovementsMutation.isPending ? 'Loading...' : 'Get AI Guide'}
                    </Button>
                  </div>
                </div>

                <div className="space-y-2">
                  {analysis.circuitImprovements.map((improvement, index) => (
                    <motion.div
                      key={index}
                      initial={{ opacity: 0, x: -20 }}
                      animate={{ opacity: 1, x: 0 }}
                      transition={{ delay: index * 0.1 + 0.6 }}
                      className="flex items-start gap-3 p-3 bg-white/70 dark:bg-gray-800/70 rounded-lg border-l-4 border-blue-300"
                    >
                      <Wrench className="w-4 h-4 text-blue-500 mt-0.5 flex-shrink-0" />
                      <span className="text-sm">{improvement}</span>
                    </motion.div>
                  ))}
                </div>

                {/* Detailed Instructions Panel */}
                {showDetailedInstructions && detailedInstructions && (
                  <motion.div
                    initial={{ opacity: 0, height: 0 }}
                    animate={{ opacity: 1, height: 'auto' }}
                    transition={{ duration: 0.3 }}
                    className="mt-4 p-4 bg-gradient-to-r from-blue-50 to-indigo-50 dark:from-blue-900/20 dark:to-indigo-900/20 rounded-lg border border-blue-200 dark:border-blue-700"
                  >
                    <div className="flex items-center gap-2 mb-3">
                      <BookOpen className="w-4 h-4 text-blue-600" />
                      <h5 className="font-semibold text-sm text-blue-800 dark:text-blue-200">
                        Detailed Circuit Instructions
                      </h5>
                    </div>
                    <div className="text-sm text-blue-900 dark:text-blue-100 whitespace-pre-wrap">
                      {detailedInstructions}
                    </div>
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => setShowDetailedInstructions(false)}
                      className="mt-3 text-xs"
                      data-testid="button-hide-instructions"
                    >
                      Hide Instructions
                    </Button>
                  </motion.div>
                )}
              </div>
            )}

            {/* Prevention Tips */}
            {analysis.preventionTips.length > 0 && (
              <>
                <Separator />
                <div className="space-y-3">
                  <div className="flex items-center gap-2">
                    <Shield className="w-4 h-4 text-green-500" />
                    <h4 className="font-semibold text-sm">Prevention Tips</h4>
                  </div>
                  <div className="space-y-2">
                    {analysis.preventionTips.map((tip, index) => (
                      <motion.div
                        key={index}
                        initial={{ opacity: 0, x: -20 }}
                        animate={{ opacity: 1, x: 0 }}
                        transition={{ delay: index * 0.1 + 0.9 }}
                        className="flex items-start gap-3 p-3 bg-white/70 dark:bg-gray-800/70 rounded-lg border-l-4 border-green-300"
                      >
                        <Shield className="w-4 h-4 text-green-500 mt-0.5 flex-shrink-0" />
                        <span className="text-sm">{tip}</span>
                      </motion.div>
                    ))}
                  </div>
                </div>
              </>
            )}

            {/* Re-analyze button */}
            <div className="pt-3">
              <Button 
                variant="outline" 
                size="sm"
                onClick={handleAnalyze}
                disabled={analyzeMutation.isPending}
                className="w-full"
                data-testid="button-reanalyze"
              >
                <RefreshCw className="w-3 h-3 mr-2" />
                Re-analyze
              </Button>
            </div>
          </motion.div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/hooks/use-toast.ts.content:
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

client/src/components/ui/toggle-group.ts.content:
"use client"

import * as React from "react"
import * as ToggleGroupPrimitive from "@radix-ui/react-toggle-group"
import { type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"
import { toggleVariants } from "@/components/ui/toggle"

const ToggleGroupContext = React.createContext<
  VariantProps<typeof toggleVariants>
>({
  size: "default",
  variant: "default",
})

const ToggleGroup = React.forwardRef<
  React.ElementRef<typeof ToggleGroupPrimitive.Root>,
  React.ComponentPropsWithoutRef<typeof ToggleGroupPrimitive.Root> &
    VariantProps<typeof toggleVariants>
>(({ className, variant, size, children, ...props }, ref) => (
  <ToggleGroupPrimitive.Root
    ref={ref}
    className={cn("flex items-center justify-center gap-1", className)}
    {...props}
  >
    <ToggleGroupContext.Provider value={{ variant, size }}>
      {children}
    </ToggleGroupContext.Provider>
  </ToggleGroupPrimitive.Root>
))

ToggleGroup.displayName = ToggleGroupPrimitive.Root.displayName

const ToggleGroupItem = React.forwardRef<
  React.ElementRef<typeof ToggleGroupPrimitive.Item>,
  React.ComponentPropsWithoutRef<typeof ToggleGroupPrimitive.Item> &
    VariantProps<typeof toggleVariants>
>(({ className, children, variant, size, ...props }, ref) => {
  const context = React.useContext(ToggleGroupContext)

  return (
    <ToggleGroupPrimitive.Item
      ref={ref}
      className={cn(
        toggleVariants({
          variant: context.variant || variant,
          size: context.size || size,
        }),
        className
      )}
      {...props}
    >
      {children}
    </ToggleGroupPrimitive.Item>
  )
})

ToggleGroupItem.displayName = ToggleGroupPrimitive.Item.displayName

export { ToggleGroup, ToggleGroupItem }

client/src/components/dashboard/notifica.content:
import { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { Bell, X, CheckCircle, XCircle, Clock, Play, ChevronDown, ChevronUp } from "lucide-react";
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { useJobStats, useJobs } from "@/hooks/use-jobs";
import { formatDistanceToNow } from "date-fns";

const cardVariants = {
  hidden: { opacity: 0, scale: 0.8, x: 100 },
  visible: { 
    opacity: 1, 
    scale: 1, 
    x: 0,
    transition: {
      type: "spring",
      stiffness: 300,
      damping: 30
    }
  }
};

const contentVariants = {
  collapsed: { height: 0, opacity: 0 },
  expanded: { 
    height: "auto", 
    opacity: 1,
    transition: {
      duration: 0.3,
      ease: "easeOut"
    }
  }
};

export function NotificationWidget() {
  const [isExpanded, setIsExpanded] = useState(false);
  const { data: stats } = useJobStats();
  const { data: jobsData } = useJobs(1, 50);
  const jobs = jobsData?.jobs || [];

  // Get recent completed jobs (last 24 hours)
  const recentCompletedJobs = jobs
    .filter(job => 
      (job.status === "done" || job.status === "failed") && 
      job.endTime && 
      new Date(job.endTime).getTime() > Date.now() - 24 * 60 * 60 * 1000
    )
    .sort((a, b) => new Date(b.endTime!).getTime() - new Date(a.endTime!).getTime())
    .slice(0, 5);

  // Get running jobs
  const runningJobs = jobs.filter(job => job.status === "running").slice(0, 3);

  // Calculate notification count
  const notificationCount = runningJobs.length + recentCompletedJobs.length;

  const getStatusIcon = (status: string) => {
    switch (status) {
      case "done": return <CheckCircle className="w-3 h-3 text-green-500" />;
      case "failed": return <XCircle className="w-3 h-3 text-red-500" />;
      case "running": return <Play className="w-3 h-3 text-blue-500" />;
      case "queued": return <Clock className="w-3 h-3 text-yellow-500" />;
      default: return <Clock className="w-3 h-3 text-gray-500" />;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case "done": return "text-green-600 bg-green-50 dark:bg-green-900/20";
      case "failed": return "text-red-600 bg-red-50 dark:bg-red-900/20";
      case "running": return "text-blue-600 bg-blue-50 dark:bg-blue-900/20";
      case "queued": return "text-yellow-600 bg-yellow-50 dark:bg-yellow-900/20";
      default: return "text-gray-600 bg-gray-50 dark:bg-gray-900/20";
    }
  };

  return (
    <motion.div
      variants={cardVariants}
      initial="hidden"
      animate="visible"
    >
      <Card className="w-80 bg-white/90 dark:bg-gray-800/90 backdrop-blur-md border-gray-200/50 dark:border-gray-700/50 shadow-lg">
        <CardHeader 
          className="p-4 cursor-pointer hover:bg-gray-50/50 dark:hover:bg-gray-700/50 transition-colors"
          onClick={() => setIsExpanded(!isExpanded)}
        >
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <div className="p-2 bg-gradient-to-r from-blue-500 to-purple-500 rounded-lg">
                <Bell className="w-4 h-4 text-white" />
              </div>
              <div>
                <h3 className="text-sm font-semibold text-gray-900 dark:text-white">
                  Notifications
                </h3>
                <p className="text-xs text-gray-500 dark:text-gray-400">
                  {notificationCount} updates
                </p>
              </div>
            </div>
            <div className="flex items-center space-x-2">
              {notificationCount > 0 && (
                <Badge className="bg-red-500 text-white text-xs">
                  {notificationCount}
                </Badge>
              )}
              <motion.div
                animate={{ rotate: isExpanded ? 180 : 0 }}
                transition={{ duration: 0.2 }}
              >
                <ChevronDown className="w-4 h-4 text-gray-400" />
              </motion.div>
            </div>
          </div>
        </CardHeader>

        <AnimatePresence>
          {isExpanded && (
            <motion.div
              variants={contentVariants}
              initial="collapsed"
              animate="expanded"
              exit="collapsed"
            >
              <CardContent className="p-0 max-h-96 overflow-y-auto">
                {/* Running Jobs */}
                {runningJobs.length > 0 && (
                  <div className="p-4 border-t border-gray-200 dark:border-gray-700">
                    <div className="flex items-center space-x-2 mb-3">
                      <Play className="w-3 h-3 text-blue-600" />
