import { useState } from 'react';
import { motion, useScroll, useTransform } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
import { 
  ArrowRight, 
  Zap, 
  Shield, 
  Code, 
  BookOpen, 
  Menu,
  X,
  Github,
  Twitter,
  Linkedin,
  Globe,
  Sparkles,
  Atom,
  Check,
  Users,
  Youtube
} from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Card, CardContent } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { useAuth } from '@/hooks/use-auth';

// Enhanced floating particles component with quantum-like effects
const FloatingParticles = () => {
  const particles = Array.from({ length: 40 }, (_, i) => ({
    id: i,
    size: Math.random() * 6 + 2,
    left: Math.random() * 100,
    top: Math.random() * 100,
    animationDelay: Math.random() * 8,
    duration: Math.random() * 15 + 10,
    color: ['blue', 'purple', 'pink', 'cyan'][Math.floor(Math.random() * 4)]
  }));

  return (
    <div className="absolute inset-0 overflow-hidden pointer-events-none">
      {particles.map((particle) => (
        <motion.div
          key={particle.id}
          className={`absolute rounded-full ${
            particle.color === 'blue' ? 'bg-blue-400/30' :
            particle.color === 'purple' ? 'bg-purple-400/30' :
            particle.color === 'pink' ? 'bg-pink-400/30' : 'bg-cyan-400/30'
          }`}
          style={{
            width: particle.size,
            height: particle.size,
            left: `${particle.left}%`,
            top: `${particle.top}%`,
            filter: 'blur(0.5px)',
            boxShadow: `0 0 ${particle.size * 2}px currentColor`
          }}
          animate={{
            y: [0, -window.innerHeight * 0.3, 0],
            x: [0, Math.sin(particle.id) * 100, 0],
            opacity: [0, 1, 0.7, 1, 0],
            scale: [0, 1, 1.2, 1, 0],
            rotate: [0, 180, 360]
          }}
          transition={{
            duration: particle.duration,
            delay: particle.animationDelay,
            repeat: Infinity,
            ease: "easeInOut"
          }}
        />
      ))}
    </div>
  );
};

