  app.patch("/api/workspace-members/:id", async (req, res) => {
    try {
      const member = await storage.updateWorkspaceMember(req.params.id, req.body);
      if (!member) {
        return res.status(404).json({ error: "Workspace member not found" });
      }
      res.json(member);
