  return (
    <div className="w-full h-[300px] bg-gradient-to-br from-purple-900 to-blue-900 rounded-lg flex items-center justify-center">
      <canvas 
        ref={canvasRef} 
        width={800} 
        height={300}
        className="max-w-full rounded-lg"
      />
    </div>
