      <div className="grid gap-4 md:grid-cols-3">
        {[
          {
            title: "Top Score",
            value: "9,850",
            desc: "By player_quantum_master",
            icon: Trophy,
            color: "text-amber-500",
            bgColor: "bg-amber-500/10",
            gradient: "from-amber-500 to-amber-600"
