          </div>

          <div className="flex items-center gap-4">
            <div className="hidden md:flex items-center gap-3 px-3 py-2 rounded-lg bg-card border border-border">
              <Clock className="h-4 w-4 text-primary" />
              <div className="text-xs">
                <div className="font-semibold text-foreground" data-testid="text-current-time">{formatTime()}</div>
                <div className="text-muted-foreground" data-testid="text-current-date">{formatDate()}</div>
              </div>
            </div>

            {userLocation && (
              <div className="hidden lg:flex items-center gap-2 px-3 py-2 rounded-lg bg-card border border-border">
                <MapPin className="h-4 w-4 text-primary" />
                <div className="text-xs">
                  <div className="font-semibold text-foreground" data-testid="text-user-location">
                    {userLocation.city}, {userLocation.country}
                  </div>
                  <div className="text-muted-foreground text-[10px]" data-testid="text-user-timezone">
                    {userLocation.timezone}
                  </div>
                </div>
              </div>
            )}

            <Button
              variant="ghost"
              size="icon"
              onClick={() => window.location.reload()}
              className="h-9 w-9"
              data-testid="button-refresh"
              title="Refresh Dashboard"
            >
              <RefreshCw className="h-4 w-4" />
            </Button>

            <div className="flex items-center gap-2 pl-3 border-l border-border">
              <div className="text-right hidden sm:block">
                <p className="text-sm font-medium" data-testid="text-admin-name">Admin User</p>
                <p className="text-xs text-muted-foreground" data-testid="text-admin-role">Super Admin</p>
              </div>
              <div className="w-9 h-9 rounded-full bg-primary/10 flex items-center justify-center">
                <span className="text-sm font-medium text-primary">A</span>
              </div>
            </div>
          </div>
        </header>

        {/* Page Content */}
        <div className="p-6">
          <Routes>
            <Route index element={<AdminAnalytics />} />
            <Route path="pricing" element={<AdminPricing />} />
            <Route path="users" element={<AdminUsers />} />
            <Route path="content" element={<AdminContent />} />
            <Route path="news" element={<AdminNews />} />
            <Route path="game-scores" element={<AdminGameScores />} />
            <Route path="audit-logs" element={<AdminAuditLogs />} />
          </Routes>
        </div>
      </main>
    </div>
  );
}

